const STORAGE_KEY = 'recently_used_emojis';
const MAX_RECENT_EMOJIS = 16;

interface EmojiPickerProps {
  onEmojiSelect: (emoji: string) => void;
  onClose: () => void;
}

export const EmojiPicker: React.FC<EmojiPickerProps> = ({ onEmojiSelect, onClose }) => {
  const [activeCategory, setActiveCategory] = useState('smileys');
  const [recentEmojis, setRecentEmojis] = useState<string[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const pickerRef = useRef<HTMLDivElement>(null);

  // Load recently used emojis from localStorage
  useEffect(() => {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      try {
        setRecentEmojis(JSON.parse(stored));
      } catch (e) {
        console.error('Failed to parse recent emojis:', e);
      }
    }
  }, []);

  // Save to localStorage
  const saveRecentEmoji = (emoji: string) => {
    setRecentEmojis((prev) => {
      const filtered = prev.filter((e) => e !== emoji);
      const updated = [emoji, ...filtered].slice(0, MAX_RECENT_EMOJIS);
      localStorage.setItem(STORAGE_KEY, JSON.stringify(updated));
      return updated;
    });
  };

  const handleEmojiClick = (emoji: string) => {
    saveRecentEmoji(emoji);
    onEmojiSelect(emoji);
  };

  // Update the recently used category
  const categories = EMOJI_CATEGORIES.map((cat) =>
    cat.id === 'recently_used' ? { ...cat, emojis: recentEmojis } : cat
  );

  // Filter emojis based on search
  const filteredCategories = searchQuery
    ? categories.map((cat) => ({
        ...cat,
        emojis: cat.emojis.filter((emoji) =>
          emoji.includes(searchQuery.toLowerCase())
        ),
      })).filter((cat) => cat.emojis.length > 0)
    : categories;

  return (
    <div
      ref={pickerRef}
      className="bg-[var(--bg-secondary)] border border-[var(--border-color)] rounded-2xl shadow-2xl w-[360px] max-h-[420px] flex flex-col overflow-hidden"
    >
      {/* Header with Search */}
      <div className="p-3 border-b border-[var(--border-color)]">
        <div className="relative">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search emoji..."
            className="w-full px-3 py-2 pl-9 bg-[var(--bg-tertiary)] border border-[var(--border-color)] rounded-xl text-sm focus:outline-none focus:border-[var(--accent-primary)] transition-colors"
          />
          <svg
            className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-[var(--text-muted)]"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
            />
          </svg>
        </div>
      </div>

      {/* Category Tabs */}
      {!searchQuery && (
        <div className="flex items-center gap-1 px-2 py-2 border-b border-[var(--border-color)] overflow-x-auto scrollbar-hide">
          {categories
            .filter((cat) => cat.emojis.length > 0 || cat.id !== 'recently_used')
            .map((category) => (
              <button
                key={category.id}
                type="button"
                onClick={() => setActiveCategory(category.id)}
                className={`flex-shrink-0 w-9 h-9 rounded-lg flex items-center justify-center text-lg transition-colors ${
                  activeCategory === category.id
                    ? 'bg-[var(--accent-primary)] text-white'
                    : 'hover:bg-[var(--bg-tertiary)] text-[var(--text-secondary)]'
                }`}
                title={category.name}
              >
                {category.icon}
              </button>
            ))}
        </div>
      )}

      {/* Emoji Grid */}
      <div className="flex-1 overflow-y-auto p-3">
        {searchQuery ? (
          // Search Results
          <div>
            {filteredCategories.map((category) => (
              <div key={category.id} className="mb-4">
                <div className="text-xs font-medium text-[var(--text-muted)] mb-2 px-1">
                  {category.name}
                </div>
                <div className="grid grid-cols-8 gap-1">
                  {category.emojis.map((emoji, idx) => (
                    <button
                      key={`${category.id}-${idx}`}
                      type="button"
                      onClick={() => handleEmojiClick(emoji)}
                      className="w-9 h-9 text-xl hover:bg-[var(--bg-tertiary)] rounded-lg transition-colors flex items-center justify-center"
                    >
                      {emoji}
                    </button>
                  ))}
                </div>
              </div>
            ))}
            {filteredCategories.length === 0 && (
              <div className="text-center py-8 text-[var(--text-muted)]">
                No emojis found
              </div>
            )}
          </div>
        ) : (
          // Category View
          <div>
            {categories
              .filter((cat) => cat.id === activeCategory)
              .map((category) => (
                <div key={category.id}>
                  <div className="text-xs font-medium text-[var(--text-muted)] mb-2 px-1">
                    {category.name}
                  </div>
                  <div className="grid grid-cols-8 gap-1">
                    {category.emojis.length > 0 ? (
                      category.emojis.map((emoji, idx) => (
                        <button
                          key={`${category.id}-${idx}`}
                          type="button"
                          onClick={() => handleEmojiClick(emoji)}
                          className="w-9 h-9 text-xl hover:bg-[var(--bg-tertiary)] rounded-lg transition-colors flex items-center justify-center"
                        >
                          {emoji}
                        </button>
                      ))
                    ) : category.id === 'recently_used' ? (
                      <div className="col-span-8 text-center py-4 text-[var(--text-muted)] text-sm">
                        No recent emojis
                      </div>
                    ) : null}
                  </div>
                </div>
              ))}
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="p-2 border-t border-[var(--border-color)] flex justify-between items-center">
        <span className="text-xs text-[var(--text-muted)]">
          Click to insert
        </span>
        <button
          type="button"
          onClick={onClose}
          className="text-xs px-3 py-1 rounded-lg hover:bg-[var(--bg-tertiary)] transition-colors text-[var(--text-secondary)]"
        >
          Close
        </button>
      </div>
    </div>
  );
};
